class GamelogRoulette extends BaseGamelogRoulette
{
	public function getRoulettelogDetails($logId, $sessionId, $playerId)
	{
		$conn = Zenfox_Partition::getInstance()->getConnections($playerId);
		Doctrine_Manager::getInstance()->setCurrentConnection($conn);
		
		$query = Zenfox_Query::create()
						->from('GamelogRoulette gr')
						->where('gr.log_id = ?', $logId)
						->andWhere('gr.session_id = ?', $sessionId)
						->andWhere('gr.player_id = ?', $playerId);
						
		try
		{
			$data = $query->fetchArray();
		}
		catch(Exception $e)
		{
			return false;
		}
		if($data)
		{
			foreach($data as $result)
			{
				return array(
					'gameId' => $result['session_id'] . '-' . $result['log_id'],
					'logId' => $result['log_id'],
					'sessionId' => $result['session_id'],
					'playerId' => $result['player_id'],
					'machineId' => $result['machine_id'],
					'runningMachineId' => $result['running_machine_id'],
					'gameFlavour' => $result['game_flavour'],
					'betString' => $result['bet_string'],
					'betAmount' => $result['bet_amount'],
					'winNumber' => $result['win_number'],
					'winString' => $result['win_string'],
					'winAmount' => $result['win_amount'],
					'datetime' => $result['datetime'],
					'amountType' => $result['amount_type'],
					'pjpWinstatus' => $result['pjp_winstatus'],
					'pjpId' => $result['pjp_id'],
					'pjpRng' => $result['pjp_rng'],
					'pjpWinAmount' => $result['pjp_win_amount'],
					'currency' => $result['wagered_currency'],
					'frontendId' => $result['frontend_id']);
			}
		}
		return NULL;
	}
}